mapper.select:
select
count(*) from alarm
where user_id=#{user_id}

SELECT a.*, r.*
FROM (
    SELECT a.*, ROWNUM rn
    FROM (
        SELECT * 
        FROM alarm 
        WHERE user_id = #{user_id}
        ORDER BY alarm_no DESC
    ) a
) b  
INNER JOIN recruit r ON b.recruit_no = r.recruit_no
WHERE b.rn BETWEEN #{startRow} AND #{endRow} 
AND r.recruit_del = 0;
